import React, { useState } from 'react'
import Button from './components/Button';

function App() {

  const [digits, setDigits] = useState(0);

  const numbers = [{
    value: 7, 
    numWord: "seven number",
  },
  { value: 8, 
    numWord: "eight number"
  },
  { value: 9, 
    numWord: "nine number"
  },
  { value: 4, 
    numWord: "four number"
  },
  { value: 5, 
    numWord: "five number"
  },
  { value: 6, 
    numWord: "six number"
  },
  { value: 3, 
    numWord: "three number"
  },
  { value: 2, 
    numWord: "two number"
  },
  { value: 1, 
    numWord: "one number"
  },
  { value: 0,
    numWord: "zero number"
  }
];
  const makeButtons = numbers.map(number => (
    <Button number={number.value} value={number.value} className={number.numWord} click={handleClick}/>
  ))

  function handleClick(event) {
    console.log(event.target.value)
    setDigits(event.target.number)
    console.log(digits)
  }

  return (
    <div className="App">
      <div className="total" value={digits}>{digits}</div>
      {makeButtons}
    </div>
  );
}

export default App;
