import React, { useState } from 'react'
import Button from './components/Button';

function App() {

  const [digits, setDigits] = useState(0);

  const numbers = [
  {
    id: "num7",
    value: 7, 
    numWord: "seven number",
  },
  { 
    id: "num8",
    value: 8, 
    numWord: "eight number"
  },
  { 
    id: "num9",
    value: 9, 
    numWord: "nine number"
  },
  { 
    id: "num4",
    value: 4, 
    numWord: "four number"
  },
  { 
    id: "num5",
    value: 5, 
    numWord: "five number"
  },
  { 
    id: "num6",
    value: 6, 
    numWord: "six number"
  },
  { 
    id: "num3",
    value: 3, 
    numWord: "three number"
  },
  { 
    id: "num2",
    value: 2, 
    numWord: "two number"
  },
  { 
    id: "num1",
    value: 1, 
    numWord: "one number"
  },
  { 
    id: "num0",
    value: 0,
    numWord: "zero number"
  }
];
  const makeButtons = numbers.map(number => (
    <Button key={number.id} number={number.value} value={number.value} className={number.numWord} click={handleClick}/>
  ))

  const mathButtons = [
    {
      id: "xx",
      value: "x",
      mathWord: "multiply"
    },
    {
      id: "++",
      value: "+",
      mathWord: "add"
    },
    {
      id: "--",
      value: "-",
      mathWord: "subtract"
    },
    {
      id: "//",
      value: "/",
      mathWord: "divide"
    }
  ];

  const makeMath = mathButtons.map(mathButton => (
    <Button value={mathButton.value} number={mathButton.value} click={handleClick} />
  ))

  function handleClick(event) {
    setDigits(event.target.value)
    console.log(digits)
  }

  return (
    <div className="App">
      <div className="total" value={digits}>{digits}</div>
      {makeButtons}
      {makeMath}
    </div>
  );
}

export default App;
